INSERT INTO `wlzx_oa_notice` (`id`, `scope`, `type`, `post_department_id`, `poster_id`, `emergence_flag`, `title`, `content`, `postTime`, `last_edit_time`, `last_editor_id`, `status`, `read_nums`) VALUES
(83, 0, '其他', 3, 722, 0, '  温岭中学2011学年第二学期德育工作计划', '<TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">一、指导思想</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">以学校工作计划为指导，不断更新德育观念，坚持德育创新，使学校德育工作与学生实际情况相结合，探索更适合学生年龄与身心特点的新举措，提升德育工作的育人质量。坚持以强化行为规范养成教育为重点，以健全德育工作管理体制为保证，以提高德育队伍管理能力为关键点，通过扎实的德育教育活动，不断增强德育工作的主动性、实效性，形成全员、全方位、全过程参与的德育模式，力争使德育工作形成我校特色。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">二、工作目标</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">1、强化学生行为习惯养成教育，提高学生自我管理能力。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">2、加强班主任队伍建设,打造一支吃苦耐劳，热爱学生，具备驾驭班级管理能力和水平的育人队伍。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">3、规范学校德育管理，以校园文化建设和学生社团活动为载体提升德育档次。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">4、培养学生有较强的法制和安全意识，无重大责任事故。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">三、工作思路</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">细——管理细化。强调计划性，每月要有主题，每周要有安排，便于班主任有的放矢地开展工作。制定各项评比检查有据可依，有过程，有激励，有总结，有提高。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">严——严格管理。严格执行学校德育各项管理规定，管理者认真履行职责，依章办事。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">实——讲求实效。开展的各项活动，要有内容、求实效，真正对学生的培养有促进作用。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">新——独特新颖。不断探索德育管理措施，寻找德育的切入点，突破口，提高德育管理效率。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">四、主要工作及措施</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">（一）加强德育队伍建设，营造全员育人氛围。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">1、畅通德育渠道，优化德育管理网络。进一步健全以班主任为核心，以德育处、年级段为主要职能部门，任课教师、学生会为主体的基本德育管理网络。使德育管理的每一举措能深入到教育的每一个终端，各项德育工作能真正落到实处。做到纲举目张，富有效率。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">2、加强师德建设，要求广大教师做到：一要有强烈的事业心和责任感，坚持既教书又育人；二要在思想境界、道德情操上堪为师表，以良好的师德风范去影响学生；三要有无私奉献的精神，树立终身为师的志向；四要有爱生至情，用真情感染学生，建立民主、平等的新型师生关系。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">3、加强班主任队伍建设。班主任是德育工作最直接的操作者，班主任素质的优劣直接影响到学校德育工作的成效。对班主任队伍，尤其是年轻班主任进行有针对性的培训，提高班主任老师的理论水平和管理水平。教会班主任科学、合法、合情、合理、有效的进行班级管理。健全和完善班主任岗位职责、工作条例、班主任工作考核评估激励机制，坚持从形式和内容两方面公正、客观地衡量班主任工作，真正起到奖优罚劣的作用，促进班主任主动认真地开展工作。开好每月一次的班主任例会，针对存在的问题和取得的成绩及时进行总结。本学期将开展班级管理经验交流会，达到相互学习、相互促进的目的。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">4、强化职能部门人员的作用，增强责任意识，提高管理水平。要不断提高业务素质，增强服务意识，各尽其责，检查做到科学准确、一视同仁，工作中讲究艺术和方法，创造性地开展并圆满完成各项工作。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">    5、充分发挥团队工作的作用，扩大班干部的影响。团员、班干部在学生中的影响是不可低估的，他们应该是同学的榜样。要定期对团员、班干部进行培训，充分发挥他们的积极性，使他们带动更多的同学一起进步。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">（二）常规教育常抓不懈</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">1、建立健全常规管理长效机制。坚持学生行为习惯、文明礼仪等常规工作常抓不懈，将文明习惯养成教育和理想前途教育作为两大重点，建立健全制度，使学校德育管理实现精细化、常态化、人文化。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">2、抓好行为规范教育。通过对学生集中进行行为规范系列教育，培养学生自我约束、自我管理的能力。通过具体的强化训练，充分发挥学生的主体作用，让学生自主组织活动、管理评价，逐步使学生从“要我这样做”转变为“我应该这样做”，使良好的行为习惯内化为自觉的行动，重视良好学习习惯的养成教育，让学生终身受益。对学生日常行规以及对班级日常管理工作的考核要继续强化，加大力度，扣紧重点，注重实效，做到要求明、检查细、考核严、奖罚清。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">3、进一步抓好班级常规管理，加强各项检查评比，深化养成教育，促进班风建设。进一步开展创建学校优秀班集体活动，通过评选激励先进、鞭策落后，形成力争上游的良好氛围。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">4、要充分利用班会、国旗下讲话、校内外活动等途径有计划有针对性地对学生进行爱国主义教育、集体主义教育、劳动教育、法制教育、安全教育、网络教育、心理健康教育和基础道德教育与基础文明养成教育，做到重点清，要求明，措施实，由知到行，环环扣紧。从规范学生行为习惯做起，培养全体学生良好的道德品质和文明行为，从提高基本素质做起，促进全体学生的全面发展。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">    5、加强值周教师和值周学生常规检查反馈、督促作用，使常规管理达到精细化。进一步巩固、完善值周工作细则，加大值周力度，从细微之处入手，强化管理，给全体同学创造良好的学习和生活环境。 </FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">6、继续开展“扶贫助学”活动，切实帮助贫困学生顺利完成学业。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">7、加强班级文化建设，争创班级特色。各班要结合班级实际制订班规，要求明确、措施得力。要发挥班、队干部的群体作用，利用他们积极的心理倾向引导他们自己管理自己，自己教育自己，提高教育的实效性。在班级中开展丰富多彩的德育教育活动，在活动中培养学生的荣誉感、集体感，活动中要给更多的学生参与的机会，培养学生的责任感和主人翁意识。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">8、大力开展安全教育。各班要把安全教育、法制教育贯穿到整个学年，常抓不懈。通过学生自我教育、自我管理，使学生树立安全意识，加强安全防范，杜绝不安全隐患，创建安全文明校园。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">（三）形成温中特色的德育教育系列，使德育教育常态化、经常化。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">学生在不同的年龄阶段，有不同的心理和生理的特点，根据学生的特点，抓好学生不同阶段的管理和教育，合理安排活动，制定循序渐进的德育养成程序，就会极大地有利于学生的健康发展。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">温中特色的教育系列，为“一条主线、两大重点、三个系列、四方面渗透”。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">一条主线：一切为了学生的健康发展</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">两个重点：习惯养成教育  理想前途教育</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">三大系列：</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">高一年级——行为规范教育  以“适应教育”为支点，以“规范行为“为目标，以“强化管理、夯实基础”为工作重点，培养学生做合格的温中人</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">主题内容：九月——规范教育、十月——爱校教育、十一月——安全教育、十二月——诚信教育、三月——环保教育、四月——榜样教育、五月——亲情教育、六月——抗挫教育</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">高二年级——传统美德教育  以“发育教育”为支点，以“陶冶情操”为目标，以“扎实管理、发展个性”为工作要求，培养学生做快乐的温中人</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">主题内容：九月——尊师教育、十月——爱国教育、十一月——法制教育、十二月——勤俭教育、三月——公德教育、四月——励志教育、五月——爱心教育、六月——自控教育</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">高三年级——理想前途教育  以“信念教育”为支点，以“追求卓越”为目标，以“精细管理、共同发展”为工作方向，培养学生做优秀的温中人</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">主题内容：九月——感恩教育、十月——责任教育、十一月——理想教育、十二月——竞争教育、三月——奋进教育、四月——意志教育、五月——信心教育</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">四方面渗透：教书育人、管理育人、活动育人、环境育人</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">扎实开展有效的德育活动。围绕德育阵地、社会实践、重大节日、学校实际开展一系列德育活动，让学生在活动中陶冶情操，在活动中提升素质。设计的三个活动系列既有侧重，也有融合，既有一定的独立性、也讲求一定的层次性，既有传承、也有创新。传承体现为继续深化感恩教育、诚信教育、励志教育等，创新体现为不同的年级，针对学生的特点，设计有侧重的教育活动，如高一的榜样教育、高二的勤俭教育、高三的责任教育等。教育形式要丰富多彩，寓教于乐。常规形式有国旗下讲话、布置黑板报、开好每月一次的主题班会；自选形式要求各年级段、各班自己设计，发挥学生的主动性和自觉性，努力打造活动精品，形成特色上报学校备案。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">（四）抓好“三结合”教育，促进学生全面健康发展</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">重视家庭、社会与学校教育的配合，利用校外德育教育基地，进一步形成德育教育的合力，举办家长学校讲座，定期召开形式多样的家长会。班主任要做好家访工作，和家长一道做好学生深入细致的思想工作。利用不同形式，沟通学生在校和家庭情况，提出学生在家、在校的学习常规要求，使学生在教师、家长的指导下，能够沿着正确的轨迹前行。通过社会实践活动，对学生进行全方位的素质教育。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">（五）加强问题学生的教育与转化</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">问题学生转化工作做到有计划，有措施，勤总结，重在落实，班主任要填写好问题学生档案。要求教师耐心细致地做好后进生的思想教育工作，做到不讲过头话，少批评、多鼓励，少指责，多帮助。特别重视单亲家庭、离异家庭、贫困家庭等特殊家庭家教工作的指导和该类家庭学生的教育和管理，让这些孩子也能享受到学校大家庭的温暖，也能象其他孩子一样健康成长。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">（六）加强心理健康教育，使学生形成健全人格</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">心理健康教育是新形势下必须重视的重要内容，要开展好学生的心理健康教育、青春期教育。平时，教师要深入到学生中去，深入到学生家庭中去，把握学生思想脉膊。做好学生心理的疏导，教师要积极创设良好的课堂氛围和保证学生心理健康发展的人文环境，将心理健康教育真正落在实处。充分利用学校的心理健康教育咨询室，保证心理健康教育活动的正常化、规范化，促进学生身心全面健康地发展。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">（七）加强德育科研，提高德育工作的科学性。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">学校要加强德育科学性，要解放思想，大胆探索，不断总结，不断完善，提高德育工作科学性。切实转变观念，提升教育理念，探索德育工作的新规律和新途径，要针对德育工作中的热点、难点问题，如网络道德教育、诚信缺失等问题，开展德育科研，研究社会形势的新变化及其对学生的影响，不断转变工作方式，更新教育内容，为德育工作注入时代的活力与特色。以人为本，强调学生的主体性，把握青少年的年龄特征、身心发展需要、存在的问题，把“管、卡、压、罚”转变到“疏、导、育、教”上来，保证德育工作的有序、渐进和科学。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">（八）加强校园文化建设，营造良好育人环境。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">大力加强校园内涵文化挖掘打造，形成格调高雅的校园环境、蓬勃向上的校园精神、规范科学的校园制度。让先进文化时时充满校园，让多彩校园处处体现教育，使学生在思想、行为、情感、习惯上受到感化和影响。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0"></FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">具体工作安排：</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">（第二学期）</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">二月</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">1、制订学校德育工作计划。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">2、修订、完善德育制度</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">3．完成上一学期的班集体考核</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">4、更新德育管理系统</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">5、班主任会议：安排以班主任为核心的全员管理和精细化管理</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">6、治理和规范学生仪表（持之以恒、常抓不懈）</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">7、德育处管理人员例会（每周一次）</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">8、德育处工作人员例会（每两周一次）</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">9、建立和健全各年级段家长管理委员会</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">10、高三家长会</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">11、宿管员会议</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">三月</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">1、主题教育月活动：高一——环保教育、高二——公德教育、高三——奋进教育。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">2、安排外籍学生的膳食及有关教育工作。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">3、举办学生干部培训班</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">4、开展学生学习方法交流会</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">5、特殊学生的建档与跟踪教育</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">6、开展绿化植树活动（社团活动）</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">7、志愿者服务活动</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">8、创“温岭市德育特色学校”</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">四月</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">1、主题教育月活动：高一——榜样教育、高二——励志教育、高三——意志教育。</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">2、召开学生民主生活会</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">3、举办班主任培训会</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">4、组织部分团员祭扫烈士陵园</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">5、校园安全大检查</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">6、召开高一、高二家长会</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><FONT FACE="宋体" SIZE="12" COLOR="#666666" LETTERSPACING="1" KERNING="0">7、组织学生春游</FONT></P></TEXTFORMAT><TEXTFORMAT LEADING="2"><P ALIGN="LEFT"><